import React, { useState } from 'react'
import { useSelector } from 'react-redux'

  const Decoded = () => {

    const results = useSelector((state) => state.questions) 
    const renderHTML = (rawHTML) => React.createElement("div", { dangerouslySetInnerHTML: { __html: rawHTML } });
    const [answerSelected, setAnswerSelected] = useState(false)
    const [selectedAnswer, setSelectedAnswer] = useState(null)
    const [options, setOptions] = useState([])
    const answer = question && question.correct_answer

    const questionIndex = useSelector((state) => state.index)


  const dispatch = useDispatch()

  const question = questions[questionIndex]

    const handleListItemClick = (event) => {
      setAnswerSelected(true)
      setSelectedAnswer(event.target.textContent)

      const getClass = option => {
        if (!answerSelected) {
          return ``;
        }

        if (option === answer) {
          return `correct`
        }

        if (option === selectedAnswer) {
          return `selected`
        }
      }
    return (
        <div className="resultpage">
            <div>
              <div>
              <ul>
        {options.map((option, i) => (
          <li key={i} onClick={handleListItemClick} className={getClass(option)}>
            {options}
          </li>
        ))}
      </ul>

              </div>
              <ul>
        {results.map((result,i)=>(
          <li key = {i} >
           <div> {<p>Question</p>} {renderHTML(result.question)} </div>
            <div>
            {<p>Your Answer</p>} {selectedAnswer}
            </div>
          <div>  {<p>Correct Answer</p>} {result.correct_answer} </div>
          </li>
        ))}
      </ul></div>
        </div>
    )
}
  }

export default Decoded
